    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        String[] curso1= new String[6];
        curso1[0]="1. Lenguajes de marcas";
        curso1[1]="2. Programacion";
        curso1[2]="3. 3. Bases de datos";
        curso1[3]="4. Sistemas";
        curso1[4]="5. FOL";
        curso1[5]="6. Entornos de desarrollo";
        
        String[] curso2= new String[6];
        curso2[0]="1. Dise√±o de Interfaces";
        curso2[1]="2. Acceso a Datos";
        curso2[2]="3. Android";
        curso2[3]="4. Empresas";
        curso2[4]="5. Ingles";
        curso2[5]="6. Programacion de procesos";
        
        if (seleccionador.getSelectedItem()=="Curso 1"){
            lista.removeAllItems();
            for (int i=0;i<curso1.length;i++){
                lista.addItem(curso1[i]);
            }
        }
        if (seleccionador.getSelectedItem()=="Curso 2"){
            lista.removeAllItems();
            for (int i=0;i<curso2.length;i++){
                lista.addItem(curso2[i]);
            }
        }      
        
    }//GEN-LAST:event_jButton2ActionPerformed

